import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReactPlayer from 'react-player';

const BACKEND_URL = 'http://localhost:8000'; // Update with your backend URL

const BhojpuriPhysicsTutor = () => {
  const [prompt, setPrompt] = useState(''); // Store the question from the user
  const [explanation, setExplanation] = useState('');
  const [quizJson, setQuizJson] = useState([]);
  const [jsCode, setJsCode] = useState('');
  const [diagramExplanation, setDiagramExplanation] = useState('');
  const [isInitialized, setIsInitialized] = useState(false);
  const [audioUrl, setAudioUrl] = useState('');

  const cleanJsCode = (gptOutput) => {
    return gptOutput.replace(/```(javascript|js)?\s*/g, '').replace('```', '').trim();
  };

  const speakText = (text) => {
    const url = `http://api.voicerss.org/?key=${process.env.REACT_APP_VOICERSS_API_KEY}&hl=en-us&src=${text}`;
    setAudioUrl(url);
  };

  const fetchExplanationAndQuiz = async () => {
    try {
      const response = await axios.post(`${BACKEND_URL}/explain`, { prompt });
      const data = response.data;

      if (data.error) {
        alert('Error: ' + data.error);
        return;
      }

      setExplanation(data.explanation);
      setQuizJson(data.quiz);
      setIsInitialized(true);
    } catch (error) {
      console.error('Error fetching explanation:', error);
    }
  };

  const fetchDiagramAndAnimation = async () => {
    try {
      const response = await axios.post(`${BACKEND_URL}/generate_diagram_code`, { prompt });
      const data = response.data;

      setJsCode(cleanJsCode(data.p5js));
      setDiagramExplanation(data.explanation);
    } catch (error) {
      console.error('Error fetching diagram and animation:', error);
    }
  };

  useEffect(() => {
    if (prompt && !isInitialized) {
      fetchExplanationAndQuiz();
      fetchDiagramAndAnimation();
    }
  }, [prompt]);

  return (
    <div>
      <h1>Bhojpuri Physics Tutor (AI-Powered)</h1>
      <p>Ask a physics question and the AI tutor will explain it in Bhojpuri with text, audio, and diagrams.</p>

      {/* Input for question */}
      <input 
        type="text" 
        placeholder="Ask a physics question..." 
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
      />
      <button onClick={fetchExplanationAndQuiz}>Ask</button>

      {/* Explanation and Audio */}
      {isInitialized && explanation && (
        <div>
          <h2>Step 1: Explanation</h2>
          <p>{explanation}</p>
          <button onClick={() => speakText(explanation)}>Listen</button>
          <audio src={audioUrl} controls />
        </div>
      )}

      {/* Diagram and Animation */}
      {jsCode && (
        <div>
          <h2>Step 2: Diagram & Animation</h2>
          <iframe
            title="p5.js Diagram"
            srcDoc={jsCode}
            width="600"
            height="500"
          />
        </div>
      )}

      {/* Quiz */}
      {quizJson.length > 0 && (
        <div>
          <h2>Step 3: Quiz</h2>
          {quizJson.map((q, index) => (
            <div key={index}>
              <p>{q.question}</p>
              <div>
                {q.options.map((option, i) => (
                  <label key={i}>
                    <input 
                      type="radio" 
                      name={`q${index}`} 
                      value={option} 
                    />
                    {option}
                  </label>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BhojpuriPhysicsTutor;
