// src/App.js or App.tsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:8000";

const cleanJsCode = (gptOutput) => {
  return gptOutput
    .replace(/\[DIAGRAM_START\]|\[DIAGRAM_END\]/g, "")
    .replace(/```(javascript|js)?\s*/g, "")
    .replace(/```/g, "")
    .trim();
};

const AudioPlayer = ({ text }) => {
  const [audioURL, setAudioURL] = useState("");

  useEffect(() => {
    const synthesize = async () => {
      const url =
        "https://translate.google.com/translate_tts?ie=UTF-8&q=" +
        encodeURIComponent(text) +
        "&tl=hi&client=tw-ob";
      const blob = await fetch(url).then((r) => r.blob());
      setAudioURL(URL.createObjectURL(blob));
    };
    synthesize();
  }, [text]);

  return audioURL ? <audio controls src={audioURL} /> : <p>Loading audio...</p>;
};

const P5Viewer = ({ jsCode }) => {
  return (
    <iframe
      title="p5js"
      srcDoc={`<html><head><script src='https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.6.0/p5.min.js'></script></head><body><script>${jsCode}</script></body></html>`}
      style={{ width: "100%", height: "500px", border: "none" }}
    />
  );
};

const Quiz = ({ questions }) => {
  const [answers, setAnswers] = useState({});

  const handleAnswer = (idx, selected) => {
    setAnswers((prev) => ({ ...prev, [idx]: selected }));
  };

  return (
    <div>
      <h3>üß† Step 5: Test Yourself with Quiz</h3>
      {questions.map((q, idx) => (
        <div key={idx} style={{ marginBottom: 24 }}>
          <p><strong>{`Q${idx + 1}. ${q.question}`}</strong></p>
          {q.options.map((opt) => (
            <div key={opt}>
              <label>
                <input
                  type="radio"
                  name={`q${idx}`}
                  value={opt}
                  checked={answers[idx] === opt}
                  onChange={() => handleAnswer(idx, opt)}
                />
                {" "}{opt}
              </label>
            </div>
          ))}
          {answers[idx] && (
            <p style={{ color: answers[idx] === q.answer ? "green" : "red" }}>
              {answers[idx] === q.answer ? "‚úÖ ‡§∏‡§π‡•Ä ‡§ú‡§µ‡§æ‡§¨!" : `‚ùå ‡§ó‡§≤‡§§‡•§ ‡§∏‡§π‡•Ä ‡§ú‡§µ‡§æ‡§¨ ‡§¨‡§æ: ${q.answer}`}
            </p>
          )}
        </div>
      ))}
    </div>
  );
};

function App() {
  const prompt = "what is third law of motion?";
  const [loaded, setLoaded] = useState(false);
  const [explanation, setExplanation] = useState("");
  const [diagramExplanation, setDiagramExplanation] = useState("");
  const [jsCode, setJsCode] = useState("");
  const [quiz, setQuiz] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const explainRes = await axios.post(`${BACKEND_URL}/explain`, { prompt });
      setExplanation(explainRes.data.explanation);
      setQuiz(explainRes.data.quiz);

      const diagramRes = await axios.post(`${BACKEND_URL}/generate_diagram_code`, { prompt });
      setJsCode(cleanJsCode(diagramRes.data.p5js));
      setDiagramExplanation(diagramRes.data.explanation);

      setLoaded(true);
    };
    fetchData();
  }, []);

  if (!loaded) return <p>Loading...</p>;

  return (
    <div style={{ padding: 24, maxWidth: 800, margin: "auto" }}>
      <h1>üìò Bhojpuri Physics Tutor (AI-Powered)</h1>
      <h3>üó£Ô∏è Transcribed Text: {prompt}</h3>

      <h3>üìñ Step 1: Explanation in Bhojpuri</h3>
      <p>{explanation}</p>
      <AudioPlayer text={explanation} />

      <h3>üñºÔ∏è Step 2: Diagram Animation</h3>
      <P5Viewer jsCode={jsCode} />

      <h3>üìò Diagram Explanation</h3>
      <p>{diagramExplanation}</p>
      <AudioPlayer text={diagramExplanation} />

      <Quiz questions={quiz} />
    </div>
  );
}

export default App;
